<?php query=("SELECT anggota_kelas.nisn,anggota_kelas.kode_kelas,data_nilai.id,data_siswa.nama,data_nilai.nisn,data_nilai.kode_mapel,data_nilai.nilai_uts,data_nilai.nilai_uas,data_nilai.nilai_ulha,data_nilai.nilai_tugas,data_nilai.nilai_akhir FROM data_nilai inner join data_siswa on data_nilai.nisn=data_siswa.nisn inner join anggota_kelas on data_nilai.nisn=anggota_kelas.nisn where anggota_kelas.kode_kelas=(SELECT kode_kelas from data_kelas where NIP='$tes' )"); ?>